/*	 - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - 	 	Tester	by Julian Harris 10/93	 	Test Module	1.0a1	<< Tester.r >>			- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - 	Description		Screen Manager Resource definitions for the Idea Tester	- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */#include "MacScreen.r"// Window ID numbers - - - - - - - - - - - - - - - - - - - - - - - - #define AboutBoxID			10000#define kErrorWindowID		10001// Whole bunch of stuff - - - - - - - - - - - - - - - - - - - - - - -#define DefaultBtnHeight	20#define DefaultBtnWidth		70#define HalfDefaultBtnWidth	(DefaultBtnWidth / 2)#define DefaultBtnV			10#define DefaultBtnH			10#define RadioBtnV			14#define BtnHGrow			26#define FldHeight			14#define LineHeight			12#define FieldPromptWidth	90#define ShortFldPrompt		65#define WhiteSpace			10#define BtnSpace			10#define DoubleWSpace		20#define ObjectInterval		5#define Geneva 				3#define plain 				0#define bold 				1#define italic				2#define StdTextInsetH		1#define StdTextInsetV		1#define FieldOffsetV		10/*	- - - - - - - - - - - - - - - - - - - - - - - - - - - - - 	ABOUT BOX (most important)	- - - - - - - - - - - - - - - - - - - - - - - - - - - - - */																resource 'Wind' (AboutBoxID, "About MacVAX CC")	{	windOneCopyOnly + windNoAutoSave + windNoGrow,	AboutBoxID,	0,	posAlert, 0, 	posCenter, 0, 	posMiddle, 250,	posMiddle, 420,	0,	200,250,	0,0,	0,0,	AboutBoxID,	"About MacVax CC"	};#define LogoHeight			32#define pict8BitGreyID		128#define pict1BitID			129resource 'Itm#' (AboutBoxID, "AboutÉ Item List"){	{		/* - - OK BUTTON - - */		itemHighlight + itemAcceptEnter,				posRelative, DefaultBtnHeight,		// top		posRelative, DefaultBtnWidth,		// left		posLeft, DefaultBtnV,				// bottom		posLeft, BtnHGrow,					// right		hSkip, 0, 0,		hSkip, 0, 0, 				noCommandKey, noKey,		0,		ButtonItem			{			0, 12, 0, teJustLeft,			"OK"			}		,				/* - - GREY LINE SEPERATOR - - */		itemNoMouse,						posLeft, LogoHeight + 10,			// top		posLeft, 10,			// left		posRelative, 0,			// bottom		posLeft, 10,			// right				hSkip, 0, 0,		hSkip, 0, 0,				noCommandKey, noKey,		0,				LineItem{ greyLine }		,				/* - - ABOUT TEXT - - */		itemNormal,						posLeft, LogoHeight + 15,			// top		posLeft, 5,			// left		posLeft, 40,			// bottom		posLeft, 5,			// right				hSkip, 0, 0,		hSkip, 0, 0,				noCommandKey, noKey,		0,							EditTextItem			{			0, 12, 0, teJustCenter,			ETuseStyles + ETstaticText + ETwrap + ETnoframe,			stdInset, stdInset, 			0,			none, none, none, none,			AboutBoxID,			0,			0,			"", ""			}		,				/* - - LOGO - - */		itemNoMouse,						posLeft, 5,			// top		posLeft, 10,			// left		posLeft, 40,			// bottom		posLeft, 10,			// right				hSkip, 0, 0,		hSkip, 0, 0,				noCommandKey, noKey,		0,				PictItem			{			pictAtLeft + pictAtTop,				{				/* These have to ordered with the most restrictive first				and least restrictive last, e.g. 24 bit -> 1 bit */				pict8BitColor,							pict8BitGreyID,				pict1Bit,								pict1BitID,				}			}	}};resource 'Wind' (kErrorWindowID, "Error Window")	{	windOneCopyOnly,	kErrorWindowID,	0,	66, 0, 	posLeft, 5, 	posLeft, 5,	33, 5,	numItems,	defaultMinHeight,defaultMinWidth,	defaultOptimalHeightItemNum,defaultOptimalWidthItemNum,	defaultMaxHeight,defaultMaxWidth,	kErrorWindowID,	"Errors"	};resource 'Itm#' (kErrorWindowID, "Error Window Item List"){	{		/* - - TEXT - - */		itemNormal,						posLeft, 0,			// top		posLeft, 0,			// left		posLeft, 0,			// bottom		posLeft, 0,			// right				hSkip, 0, 0,		hSkip, 0, 0,				noCommandKey, noKey,		0,					EditTextItem		{			4, 9, 0, teJustLeft,			ETverticalScroll + ETHorizontalScroll + ETStaticText + ETviewOnly + ETGrowIcon,			stdInset, stdInset, 			unlimited,			none, none, none, none,					0,			0,			2000,			"", " "		}	}};